              // "style-loader",
              // js文件中的css，单独创建成css文件
              miniCssExtractPluginLoaderConfig,
              "css-loader",
              postcssLoaderConfig,
            ]
          },
          {
            test: /.less$/,
            use: [
              // "style-loader",
              // js文件中的css，单独创建成css文件
              miniCssExtractPluginLoaderConfig,
              "css-loader",
              postcssLoaderConfig,
